    @Test
    public void serialize_Signing_Normal_Address() {

        ECKeyPair keyPair = ECKeyPair.create(Numeric.hexStringToByteArray("9117f4bf9be717c9a90994326897f4243503accd06712162267e77f18b49c3a3"));

        RawTransaction tUnsigned = new ContractTransaction.Builder()
                .inputs(Arrays.asList(new RawTransactionInput("65827ac7308f401dfe110555b41b967e3c1177134bd977a21ca036e703ab05d4", 0)))
                .outputs(Arrays.asList(
                        new RawTransactionOutput(NEOAsset.HASH_ID, "10.0", "AK2nJJpJr6o664CWJKi1QRXjqeic2zRp8y"),
                        new RawTransactionOutput(NEOAsset.HASH_ID, "90.0", "AKYdmtzCD6DtGx16KHzSTKY8ji29sMTbEZ")))
                .build();

        byte[] tUnsignedArray = tUnsigned.toArray();
        assertEquals(
                "",
                Numeric.toHexStringNoPrefix(tUnsignedArray));

        byte[] signature = Sign.signMessage(tUnsignedArray, keyPair).getConcatenated();
        tUnsigned.addScript(
                Arrays.asList(new RawInvocationScript(signature)),
                new RawVerificationScript(Arrays.asList(keyPair.getPublicKey()), 1)
        );

        tUnsignedArray = tUnsigned.toArray();
        LOG.info("serialized: " + Numeric.toHexStringNoPrefix(tUnsignedArray));

        assertEquals(
                "",
                Numeric.toHexStringNoPrefix(tUnsignedArray));
    }

    @Test
    public void serialize_Signing_CheckMultiSig_10_PublicKeys_7_Signatures() {

        ECKeyPair ecKeyPair1 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("Kx9xMQVipBYAAjSxYEoZVatdVQfhYHbMFWSYPinSgAVd1d4Qgbpf"));
        ECKeyPair ecKeyPair2 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("KzbKux44feMetfqdA5Cze9FNAkydRmphoFKnK5TGDdEQ8Nv1poXV"));
        ECKeyPair ecKeyPair3 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L3hxLFUsNDmkzW6QoLH2PGc2DqGG5Kj1gCVwmr7duWJ9FReYWnjU"));
        ECKeyPair ecKeyPair4 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("Kwc1zMAmYHYQ77jZuopaEgL7FejdyDRxd9jpPQJQFkUH39MQpab9"));
        ECKeyPair ecKeyPair5 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L4LefFC58PPsPyacCLwGJ7RhuPZLnfLdQUiwNyW6gu11JkSRyx73"));
        ECKeyPair ecKeyPair6 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L4ToFoNfJR1XwzSEHVQonxjmLfoRW91oVLZ5hGGLakkkKUMHanWC"));
        ECKeyPair ecKeyPair7 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L1RirHWwQcwEcsUVmF6m4s9wUB8zufUXEHrHmcvuP4cuNTXA1nSM"));
        ECKeyPair ecKeyPair8 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L1CcDgYLRT1VzGQEzHATyHwPoYHwGTDdD4ugJYESYfGehZ6AtnHw"));
        ECKeyPair ecKeyPair9 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("L2xUnihSNBTh8jrYzLyrxMfFwDbPp1HvDe92r7HxsZQ8rL4NktfE"));
        ECKeyPair ecKeyPair10 = ECKeyPair.create(WIF.getPrivateKeyFromWIF("KyRPzRthggPA3SSRTBgma3VyjKhS3yhSM2jsKVrLRfVkFP5g5bQi"));

        // the multiSig address should be "AJqgaX57U9ua5WxBKfA27wbfEgtR8HwER3"
        String multiSigAddress = Keys.getMultiSigAddress(
                7,
                ecKeyPair1.getPublicKey(),
                ecKeyPair2.getPublicKey(),
                ecKeyPair3.getPublicKey(),
                ecKeyPair4.getPublicKey(),
                ecKeyPair5.getPublicKey(),
                ecKeyPair6.getPublicKey(),
                ecKeyPair7.getPublicKey(),
                ecKeyPair8.getPublicKey(),
                ecKeyPair9.getPublicKey(),
                ecKeyPair10.getPublicKey()
        );
        LOG.info("multiSigAddress: " + multiSigAddress);

        RawVerificationScript verificationScript = Keys.getVerificationScriptFromPublicKeys(
                7,
                ecKeyPair1.getPublicKey(),
                ecKeyPair2.getPublicKey(),
                ecKeyPair3.getPublicKey(),
                ecKeyPair4.getPublicKey(),
                ecKeyPair5.getPublicKey(),
                ecKeyPair6.getPublicKey(),
                ecKeyPair7.getPublicKey(),
                ecKeyPair8.getPublicKey(),
                ecKeyPair9.getPublicKey(),
                ecKeyPair10.getPublicKey()
        );

        RawTransaction tUnsigned = new ContractTransaction.Builder()
                .inputs(Arrays.asList(new RawTransactionInput("9feac4774eb0f01ab5d6817c713144b7c020b98f257c30b1105062d434e6f254", 0)))
                .outputs(Arrays.asList(
                        new RawTransactionOutput(NEOAsset.HASH_ID, "100.0", "AK2nJJpJr6o664CWJKi1QRXjqeic2zRp8y"),
                        new RawTransactionOutput(NEOAsset.HASH_ID, "900.0", multiSigAddress)))
                .build();

        byte[] tUnsignedArray = tUnsigned.toArray();

        assertEquals(
                "",
                Numeric.toHexStringNoPrefix(tUnsignedArray)
        );

        // add the signatures -- order here is important!
        List<RawInvocationScript> rawInvocationScriptList = new ArrayList<>();
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair1)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair2)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair3)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair4)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair5)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair6)));
        rawInvocationScriptList.add(new RawInvocationScript(Sign.signMessage(tUnsignedArray, ecKeyPair7)));

        tUnsigned.addScript(rawInvocationScriptList, verificationScript);

        byte[] tSignedArray = tUnsigned.toArray();
        LOG.info("Transaction serialized: " + Numeric.toHexStringNoPrefix(tSignedArray));

        assertEquals(
                "",
                Numeric.toHexStringNoPrefix(tSignedArray)
        );
    }

